import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import cup1 from './img/купольная 1.jpg'
import cup2 from './img/купольная2.jpg'
import cil1 from './img/цилиндрическая1.jpg'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      orders: [],
      cameras: [
        {
          id: 1,
          title: 'Купольная камера DAHUA',
          img: cup1,
          type: 'IP-видеокамера',
          resolution: '2 MP',
          lens: '2.8 mm',
          price: '29999'
        },
        {
          id: 2,
          title: 'Купольная камера DAHUA',
          img:cup2,
          type: 'IP-видеокамера',
          resolution: '2 MP',
          lens: '2.8 mm',
          price: '27999'
        },
        {
          id: 3,
          title: 'Цилиндрическая камера DAHUA',
          img:cil1,
          type: 'IP-видеокамера',
          resolution: '2 MP',
          lens: '2.8 mm',
          price: '27999'
        },
      ]
    }
    this.addToOrder = this.addToOrder.bind(this)
    this.deleteOrder = this.deleteOrder().bind(this)
  }

  render() {
    return (
        <div className="wrapper">
          <Header orders={this.state.orders} onDelete = {this.deleteOrder}/>

          <Items items={this.state.cameras} onAdd={this.addToOrder}/>
          <Footer />
        </div>
    );
