import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import Categories from "./components/Categories";
import ShowFullItem from "./components/ShowFullItem";
import cup1 from './img/купольная 1.jpg'
import cup2 from './img/купольная2.jpg'
import cil1 from './img/цилиндрическая1.jpg'
import About from "./components/About";



class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      orders: [],
      currentItems: [],
      items: [
        {
          id: 1,
          title: 'Купольная камера DAHUA',
          img: cup1,
          type: 'IP-видеокамера',
          resolution: '2 MP',
          lens: '2.8 mm',
          price: '29999'
        },
        {
          id: 2,
          title: 'Купольная камера DAHUA',
          img:cup2,
          type: 'IP-видеокамера',
          resolution: '2 MP',
          lens: '2.8 mm',
          price: '27999'
        },
        {
          id: 3,
          title: 'Цилиндрическая камера DAHUA',
          img:cil1,
          type: 'IP-видеокамера',
          resolution: '2 MP',
          lens: '2.8 mm',
          price: '27999'
        },
      ],
      showFullItem :false,
      fullItem: {},

    }
    this.state.currentItems = this.state.items
    this.addToOrder = this.addToOrder.bind(this)
    this.deleteOrder = this.deleteOrder.bind(this)
    this.chooseCategory = this.chooseCategory.bind(this)
    this.onShowItem = this.onShowItem.bind(this)
  }



  render() {
    return (
        <Router>
          <div className="wrapper">
            <Header  orders={this.state.orders} onDelete = {this.deleteOrder}/>
            {/*<Routes>*/}
            {/*  <Route path="/about" element={<About /> } />*/}
            {/*</Routes>*/}
            <Categories chooseCategory={this.chooseCategory}/>
            <Items onShowItem={this.onShowItem} items={this.state.currentItems} onAdd={this.addToOrder}/>
            {this.state.showFullItem && <ShowFullItem onAdd={this.addToOrder} onShowItem={this.onShowItem} item={this.state.fullItem}/>}
            <Footer />
          </div>
        </Router>
    );
  }


  onShowItem(item) {
    this.setState({fullItem: item})
    this.setState({showFullItem: !this.state.showFullItem})
  }

  chooseCategory(category) {
    if(category === 'all' ) {
      this.setState({currentItems: this.state.items})
      return
    }
    this.setState({
      currentItems: this.state.items.filter(el => el.type === category)
    })
  }
  deleteOrder(id) {
    this.setState({orders: this.state.orders.filter(el => el.id !==id)})
  }

  addToOrder(item) {
    let isInArray = false
    this.state.orders.forEach(el => {
      if (el.id === item.id)
        isInArray = true
    })
    if(!isInArray)
    this.setState({orders:[...this.state.orders, item]})
}
}

export default App;
