import React from "react";
import { useState } from "react";
import '../styles/contacts.css';

const Contacts = ({ onClose }) => {
    const [contacts, setContacts] = useState({
        name: "Lookwider",
        address: "г. Алматы, ул. Панфилова, 123",
        phone: "+7 (727) 317-57-14",
        email: "sales1@lookwider.kz"
    });

    const [feedback, setFeedback] = useState({
        name: "",
        email: "",
        message: ""
    });

    const handleSubmitFeedback = () => {
        // Сохраняем данные формы в локальном хранилище
        localStorage.setItem("feedback-name", feedback.name);
        localStorage.setItem("feedback-email", feedback.email);
        localStorage.setItem("feedback-message", feedback.message);

        // Отображаем сообщение об успешной отправке
        setFeedback({
            name: "",
            email: "",
            message: ""
        });
    };

    return (
        <div className="contacts">
            <div>

                <h1>CONTACTS</h1>

                <div className="contacts-info">
                    <h3>{contacts.name}</h3>
                    <p>{contacts.address}</p>
                    <p>{contacts.phone}</p>
                    <p>{contacts.email}</p>
                </div>

                <form onSubmit={handleSubmitFeedback}>
                    <div className="form-group">
                        <label htmlFor="name">Имя</label>
                        <input type="text" id="name" name="name" value={feedback.name} />
                    </div>

                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input type="email" id="email" name="email" value={feedback.email} />
                    </div>

                    <div className="form-group">
                        <label htmlFor="message">Сообщение</label>
                        <textarea id="message" name="message" rows="5" value={feedback.message} />
                    </div>

                    <button type="submit">Отправить</button>
                </form>

                <button className="close-icon" onClick={handleNavigateHome}>
                    &#10006;
                </button>

            </div>
        </div>
    );
};

export default Contacts;
