import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      orders: [],
      cameras: [
        {
          id: 1,
          title: 'Купольная камера DAHUA',
          img:'dome-camera-hiwatch-.jpeg',
          type: 'IP-видеокамера',
          resolution: '2 MP',
          lens: '2.8 mm',
          price: '29999'
        },
        {
          id: 2,
          title: 'Купольная камера DAHUA',
          img:'купольная2.jpg',
          type: 'IP-видеокамера',
          resolution: '2 MP',
          lens: '2.8 mm',
          price: '27999'
        },
        {
          id: 3,
          title: 'Цилиндрическая камера DAHUA',
          img:'цилиндрическая1.jpg',
          type: 'IP-видеокамера',
          resolution: '2 MP',
          lens: '2.8 mm',
          price: '27999'
        },
      ]
    }
    this.addToOrder = this.addToOrder.bind(this)
  }

  render() {
    return (
        <div className="wrapper">
          <Header />

          <Items items={this.state.cameras} />
          <Footer />
        </div>
    );
  }
  addToOrder(item) {
    this.setState({orders:[...this.state.orders, item]})
}
}

export default App;
