import React, {useState} from 'react'
import { FaShopify } from "react-icons/fa";
import { Link } from "react-router-dom";
import Order from "./Order";
import About from "./About";
import Contacts from "./Contacts";


const showOrders = (props) => {
    let summa = 0
    props.orders.forEach(el => summa += Number.parseFloat(el.price))
    return (<div>
        {props.orders.map(el => (
            <Order onDelete= {props.onDelete} key={el.id} item={el} />
        ))}
        <p className='summa'> Сумма: {new Intl.NumberFormat().format(summa)}$ </p>
    </div>)
}

const showNothing = () => {
    return(<div className='empty'>
        <h2>Товаров нет!</h2>
    </div>)
}


export default function Header(props) {
    let [cartOpen, setCartOpen] = useState(false)
    let [aboutOpen, setAboutOpen] = useState(false)
    let [contactOpen, setContactOpen] = useState(false)
    const onCloseAbout = () => {
        setAboutOpen(false);
    };

    const onCloseContact = () => {
        setAboutOpen(false);
    };
    return (
        <header>
            <div>
                <span className='logo'>AIVIDEON</span>
                <ul className='nav'>
                    <li><Link to="/about">About</Link></li>
                    <li><Link to="/contacts">Contacts</Link></li>
                    <li>Cabinet</li>
                </ul>
                <FaShopify onClick={()=> setCartOpen(cartOpen = !cartOpen)} className={`shop-cart-button ${cartOpen && 'active'}` }/>
                {cartOpen && (
                    <div className='shop-cart'>

                        {props.orders.length > 0 ?
                            showOrders(props) : showNothing()
                        }
                    </div>
                )}

                {aboutOpen && <About onClose={onCloseAbout} orders={props.orders} onDelete={props.onDelete} />}

                {contactOpen && <Contacts onClose={onCloseContact} orders={props.orders} onDelete={props.onDelete} />}
            </div>
            <div className='presentation'></div>
        </header>
    )
}
